			groupId = 'com.rayyau.eshop'
			artifactId = 'eshop-pymt-svc'
			version = '0.0.1-SNAPSHOT'

			from components.java
		}
	}
	repositories {
		maven {
			url = uri("$System.env.HOME/.m2/repository") // Local repository directory
		}
	}
}

